import{$ as i,U as a,S as s,as as t}from"./chunks/framework.BXaA7olX.js";const u=JSON.parse('{"title":"设置 Git 默认编辑器","description":"","frontmatter":{},"headers":[],"relativePath":"Application/Git/Command/Git_Editor.md","filePath":"Application/Git/Command/Git_Editor.md","lastUpdated":1701054425000}'),e={name:"Application/Git/Command/Git_Editor.md"},l=t('<h1 id="设置-git-默认编辑器" tabindex="-1">设置 Git 默认编辑器 <a class="header-anchor" href="#设置-git-默认编辑器" aria-label="Permalink to &quot;设置 Git 默认编辑器&quot;">​</a></h1><p>本部分我们讨论如何配置您的 <code>Git</code> 的默认文本编辑器，配置完成后，您将得到以下功能：</p><ul><li>这样当你需要编辑 Git 的配置文件或提交信息时，Git 会自动打开你选择的文本编辑器</li></ul><h2 id="使用-visual-studio-code-作为你的编辑器" tabindex="-1">使用 <code>Visual Studio Code</code> 作为你的编辑器 <a class="header-anchor" href="#使用-visual-studio-code-作为你的编辑器" aria-label="Permalink to &quot;使用 `Visual Studio Code` 作为你的编辑器&quot;">​</a></h2><ol><li><p>安装 <code>VS Code</code></p></li><li><p>打开终端并输入以下命令，将 VS Code 设置为你的默认文本编辑器：</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">git</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> config</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --global</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> core.editor</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;code --wait&quot;</span></span></code></pre></div></li></ol><p>这样，当你需要使用 Git 编辑一个文件时，<code>Git</code> 会自动打开 <code>VS Code</code>，并在你保存并关闭文件后继续执行 Git 的操作。</p><h2 id="使用-sublime-text-作为你的编辑器" tabindex="-1">使用 <code>Sublime Text</code> 作为你的编辑器 <a class="header-anchor" href="#使用-sublime-text-作为你的编辑器" aria-label="Permalink to &quot;使用 `Sublime Text` 作为你的编辑器&quot;">​</a></h2><ol><li><p>安装 <code>Sublime Text</code></p></li><li><p>输入以下命令：</p><div class="vp-code-group vp-adaptive-theme"><div class="tabs"><input type="radio" name="group-h5bck" id="tab-xlLHaeB" checked><label for="tab-xlLHaeB">Linux</label><input type="radio" name="group-h5bck" id="tab-F5oaS1H"><label for="tab-F5oaS1H">Windows</label></div><div class="blocks"><div class="language-sh vp-adaptive-theme active"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">git</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> config</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --global</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> core.editor</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;subl -n -w&quot;</span></span></code></pre></div><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">git</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> config</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --global</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> core.editor</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;&#39;C:/Program Files (x86)/sublime text 3/subl.exe&#39; -w&quot;</span></span></code></pre></div></div></div></li></ol><h2 id="使用-textmate-作为编辑器" tabindex="-1">使用 <code>TextMate</code> 作为编辑器 <a class="header-anchor" href="#使用-textmate-作为编辑器" aria-label="Permalink to &quot;使用 `TextMate` 作为编辑器&quot;">​</a></h2><ol><li><p>安装 <code>TextMate</code></p></li><li><p>安装 <code>TextMate</code> 的 <code>mate</code> shell 实用程序。</p></li><li><p>输入以下命令：</p><div class="language-shell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">git</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> config</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --global</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> core.editor</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;mate -w&quot;</span></span></code></pre></div></li></ol><h2 id="使用-notepad-作为编辑器" tabindex="-1">使用 <code>Notepad++</code> 作为编辑器 <a class="header-anchor" href="#使用-notepad-作为编辑器" aria-label="Permalink to &quot;使用 `Notepad++` 作为编辑器&quot;">​</a></h2><ol><li><p>安装 <code>Notepad++</code></p></li><li><p>输入以下命令：</p><div class="language-shell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">git</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> config</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --global</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> core.editor</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;&#39;C:/Program Files (x86)/Notepad++/notepad++.exe&#39; -multiInst -notabbar -nosession -noPlugin&quot;</span></span></code></pre></div></li></ol><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to &quot;参考&quot;">​</a></h2><ul><li><a href="https://docs.github.com/en/get-started/getting-started-with-git/associating-text-editors-with-git" target="_blank" rel="noreferrer">Associating text editors with Git - GitHub Docs</a></li></ul>',14),o=[l];function d(h,n,p,c,r,k){return s(),a("div",null,o)}const F=i(e,[["render",d]]);export{u as __pageData,F as default};
