<span class="line"><span style="color:#FFCB6B;">git</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">lfs</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">track</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">./README.md</span><span style="color:#89DDFF;">&quot;</span></span>
