import{_ as e,o as t,c as o,R as r,ad as a}from"./chunks/framework.iP_g6UYo.js";const N=JSON.parse('{"title":"终端 Nerd Font 字体支持","description":"","frontmatter":{},"headers":[],"relativePath":"Topic/Windows/Beautify/Nerd-Fonts-Supply.md","filePath":"Topic/Windows/Beautify/Nerd-Fonts-Supply.md","lastUpdated":1683529950000}'),n={name:"Topic/Windows/Beautify/Nerd-Fonts-Supply.md"},d=r('<h1 id="终端-nerd-font-字体支持" tabindex="-1">终端 Nerd Font 字体支持 <a class="header-anchor" href="#终端-nerd-font-字体支持" aria-label="Permalink to &quot;终端 Nerd Font 字体支持&quot;">​</a></h1><h2 id="-nerd-fonts-介绍" tabindex="-1"> Nerd Fonts 介绍 <a class="header-anchor" href="#-nerd-fonts-介绍" aria-label="Permalink to &quot; Nerd Fonts 介绍&quot;">​</a></h2><p>Nerd Fonts 常用来在终端下显示各种图标，本人一直在用 <code>Arch</code> + <code>OMZ</code> + <code>JetBrains Mono Nerd Font</code> 的组合来美化我的终端。</p><p>同时，在 Windows 平台下的 Linux 子系统中，当我们使用类似于 <a href="https://github.com/romkatv/powerlevel10k" target="_blank" rel="noreferrer"><code>powerlevel10k</code></a> 等终端主题的时候，会遇到无法显示图标的问题，这时候我们就需要 Nerd Fonts 来帮助我们显示这些图标，从而使得自己的终端更加好看。</p><h2 id="-nerd-fonts-下载与安装" tabindex="-1"> Nerd Fonts 下载与安装 <a class="header-anchor" href="#-nerd-fonts-下载与安装" aria-label="Permalink to &quot; Nerd Fonts 下载与安装&quot;">​</a></h2><p>这里推荐大家去 <a href="https://www.nerdfonts.com/" target="_blank" rel="noreferrer">Nerd Fonts 官网</a>下载自己想要的字体，这里以 <code>JetBrainsMono Nerd Font</code> 为示例作讲解。</p><p>我们直接在上面的官网中找到相关的字体进行下载，下载完成后会得到一个压缩包，解压它并将文件夹内所有的ttf字体右键进行安装（或者你也可以仅仅选择自己喜欢的某一种类型的字体，仅安装一个也可以）。</p><h2 id="-终端字体设置" tabindex="-1"> 终端字体设置 <a class="header-anchor" href="#-终端字体设置" aria-label="Permalink to &quot; 终端字体设置&quot;">​</a></h2><p>安装完对应字体后，我们打开 <code>Windows Terminal</code> ,找到设置并进入外观页面进行字体设置，设置为对应 Nerd Font 字体即可。</p><p><img src="'+a+'" alt="终端NerdFont设置"></p><p>设置完成后，重启终端即可正常显示一些特殊图标。</p><blockquote><p>Nerd Fonts 图标查询可以访问 <a href="https://www.nerdfonts.com/cheat-sheet" target="_blank" rel="noreferrer">Cheat Sheet</a></p></blockquote>',12),s=[d];function c(i,p,l,h,_,f){return t(),o("div",null,s)}const m=e(n,[["render",c]]);export{N as __pageData,m as default};
